document.addEventListener("DOMContentLoaded",(()=>{const t=document.querySelector("form");t.addEventListener("submit",(e=>{e.preventDefault(),formData=new FormData(e.target),data={};for(let t of formData.entries())data[t[0]]=t[1];fetch(t.action,{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify(data)})}))}));